import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { setProducts } from './productSlice'; // Ürünleri yönetmek için oluşturduğunuz slice

const Home = () => {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products.items);
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    price: '',
    stockQuantity: '',
    category: '',
    brand: '',
    dimensions: '',
    weight: '',
    color: '',
    imageUrl: '',
  });

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/products');
        dispatch(setProducts(response.data));
      } catch (error) {
        toast.error('Ürünler yüklenirken bir hata oluştu!');
      }
    };

    fetchProducts();
  }, [dispatch]);

  const handleInputChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleAddProduct = async () => {
    try {
      await axios.post('http://localhost:8080/api/products', newProduct);
      toast.success('Ürün başarıyla eklendi!');
      // Ürünleri yeniden yükle
      const response = await axios.get('http://localhost:8080/api/products');
      dispatch(setProducts(response.data));
    } catch (error) {
      toast.error('Ürün eklenirken bir hata oluştu!');
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/products/${id}`);
      toast.success('Ürün başarıyla silindi!');
      // Ürünleri yeniden yükle
      const response = await axios.get('http://localhost:8080/api/products');
      dispatch(setProducts(response.data));
    } catch (error) {
      toast.error('Ürün silinirken bir hata oluştu!');
    }
  };

  return (
    <div className="container mx-auto px-4">
      <h1 className="text-4xl font-bold my-4">Mobilya Ürünleri</h1>
      
      {/* Ürün Ekleme Formu */}
      <div className="my-4 p-4 bg-gray-100 rounded-lg shadow-md">
        <h2 className="text-2xl font-bold mb-4">Yeni Ürün Ekle</h2>
        <input
          type="text"
          name="name"
          placeholder="Ürün Adı"
          value={newProduct.name}
          onChange={handleInputChange}
          className="block mb-2 p-2 border rounded"
        />
        <textarea
          name="description"
          placeholder="Açıklama"
          value={newProduct.description}
          onChange={handleInputChange}
          className="block mb-2 p-2 border rounded"
        />
        <input
          type="number"
          name="price"
          placeholder="Fiyat"
          value={newProduct.price}
          onChange={handleInputChange}
          className="block mb-2 p-2 border rounded"
        />
        {/* Diğer inputlar */}
        <button
          onClick={handleAddProduct}
          className="mt-4 bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-700"
        >
          Ürün Ekle
        </button>
      </div>

      {/* Ürün Listesi */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {products.map((product) => (
          <div key={product.id} className="bg-white shadow-md rounded-lg p-4">
            <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover rounded-t-lg" />
            <h2 className="text-xl font-bold mt-4">{product.name}</h2>
            <p className="text-gray-700">{product.description}</p>
            <p className="text-lg font-bold text-green-600">${product.price}</p>
            <p className="text-sm text-gray-500">Stok Miktarı: {product.stockQuantity}</p>
            <button
              onClick={() => handleDeleteProduct(product.id)}
              className="mt-4 bg-red-500 text-white py-2 px-4 rounded hover:bg-red-700"
            >
              Ürünü Sil
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
