import "./PostProduct.css";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from 'axios';
import { useState, useEffect } from "react";



const PostProduct = () => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    stockQuantity: "",
    categoryId: "",
    brand: "",
    color: "",
  });

//burdan
  const [categories, setCategories] = useState([]);

  useEffect(() => {
      // Kategorileri API'den çekmek için istek gönderiyoruz
      axios.get('/api/categories')  // Kategori listesini çekeceğiniz backend endpoint'i buraya yazın
          .then(response => {
              setCategories(response.data);
          })
          .catch(error => {
              console.error('Error fetching categories:', error);
          });
  }, []);


  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

//   const handleSubmit = (event) => {
//     event.preventDefault();
//     console.log(formData);
//     // Ürün oluşturma işlemi burada yapılabilir
// };
//buraya yazilib


  return (
    <>
      <div className="center-form">
        <h1>Post New Product</h1>
        <Form onS>
          <Form.Group controlId="formBasicName">
            <Form.Control
              type="text"
              name="name"
              placeholder="Enter name"
              value={formData.name}
              onChange={handleInputChange}
            />
          </Form.Group>

          <Form.Group controlId="formBasicName">
            <Form.Control
              type="text"
              name="description"
              placeholder="Enter description"
              value={formData.description}
              onChange={handleInputChange}
            />
          </Form.Group>

          <Form.Group controlId="formBasicName">
            <Form.Control
              type="number"
              name="price"
              placeholder="Enter price"
              value={formData.price}
              onChange={handleInputChange}
            />
          </Form.Group>

          <Form.Group controlId="formBasicName">
            <Form.Control
              type="number"
              name="stockQuantity"
              placeholder="Enter stockQuantity"
              value={formData.stockQuantity}
              onChange={handleInputChange}
            />
          </Form.Group>

          <Form.Group controlId="formBasicCategoryId">
                        <Form.Control
                            as="select"
                            name="categoryId"
                            value={formData.categoryId}
                            onChange={handleInputChange}
                        >
                            <option value="">Select a category</option>
                            {categories.map(category => (
                                <option key={category.id} value={category.id}>
                                    {category.name}
                                </option>
                            ))}
                        </Form.Control>
                    </Form.Group>

          <Form.Group controlId="formBasicName">
            <Form.Control
              type="text"
              name="brand"
              placeholder="Enter brand"
              value={formData.brand}
              onChange={handleInputChange}
            />
          </Form.Group>

          <Form.Group controlId="formBasicName">
            <Form.Control
              type="text"
              name="color"
              placeholder="Enter color"
              value={formData.brand}
              onChange={handleInputChange}
            />
          </Form.Group>

          <Button variant="primary" type="submit" className="w-100">
            Post Product
          </Button>
        </Form>
      </div>
    </>
  );
};

export default PostProduct;
