import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { FcApproval, FcCancel } from "react-icons/fc";
import { API } from '../utils/instance';
import { toast } from 'react-hot-toast';

const RegisterForm = () => {

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword,setConfirmPassword] = useState("");
  const [confirm, setConfirm] = useState(true);

  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    if (id === "username") {
      setUsername(value);
    }
    else if (id === "email") {
      setEmail(value);
    }
    else if (id === "password") {
      setPassword(value);
    } else if (id === "confirmPassword") { // ID'yi 'confirmPassword' olarak değiştirdim
      setConfirmPassword(value);
      if (value !== password) {
        setConfirm(false);
      } else { setConfirm(true); }
    }
  }

  const handleSubmit = (e) => {
    e.preventDefault();
   
    API.post("/register", {
        username: username,
        email: email,
        password: password
    }).then((response) => {   
      navigate("/register/verify");
    }).catch((error) => {
      toast.error(error.response.data);
    });
  };

  return (
    <form onSubmit={handleSubmit} className='flex px-16 py-12 flex-col gap-3 min-w-[60%] shadow-2xl border border-gray-300 rounded-2xl bg-white'>
      <label htmlFor="username" className='flex flex-col gap-1'>
        İstifadəçi adı
        <input id='username' onChange={handleInputChange} value={username} required type="text" className='p-3 border border-gray-500 shadow-sm bg-gray-200' />
      </label>
      <label htmlFor="email" className='flex flex-col gap-1'>
        Elektron poçt
        <input id='email' onChange={handleInputChange} value={email} required type="email" className='p-3 border border-gray-500 shadow-sm bg-gray-200' />
      </label>
      <label htmlFor="password" className='flex flex-col gap-1'>
        Şifrə
        <input id='password' onChange={handleInputChange} value={password} required type="password" className='p-3 border border-gray-500 shadow-sm bg-gray-200' minLength={8} placeholder='Minimum uzunluq: 8' />
      </label>
      <label htmlFor="confirmPassword" className='flex flex-col gap-1'> {/* ID'yi 'confirmPassword' olarak değiştirdim */}
        <div className='flex items-center'>
          Şifrəni təkrarla {confirmPassword.length < 2 ? '' : !confirm ? <FcCancel /> : <FcApproval />}
        </div>
        <input id='confirmPassword' onChange={handleInputChange} value={confirmPassword} required type="password" className='p-3 border border-gray-500 shadow-sm bg-gray-200' />
      </label>
      <button className='bg-[#FC2E20] rounded py-2 shadow-sm mt-2 text-white text-lg hover:bg-blue-800' disabled={!confirm}>
        Qeydiyyat
      </button>
      <label className='text-center text-gray-400'>
        və ya
      </label>
      <Link className='text-center hover:font-bold' to="/login">Giriş et</Link>
    </form>
  )
}

export default RegisterForm;
