import { Link, useNavigate } from 'react-router-dom';
import { setAuth } from '../auth/authSlice';
import { API } from '../utils/instance';

const LoginForm = () => {

  const [username, setUsername] = useState(""); // 'emaill' yerine 'username'
  const [password, setPassword] = useState(""); // 'passwordl' yerine 'password'

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();

    API.post('/login', {
      username: username, // 'email' yerine 'username'
      password: password
    }).then((response) => {
      toast.success(`${response.data.username} xo≈ü g…ôldin`, { icon: 'üëè' });
