import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Home.css'; // CSS dosyasını unutmayın

const Home = () => {
  const [products, setProducts] = useState([]);
  // const [categories, setCategories] = useState([]); // Geçici olarak yorum satırına alın

  useEffect(() => {
    // Ürünleri API'den çek
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/products'); // Ürün endpointi
        setProducts(response.data);
        console.log("Products:", response.data); // Verileri kontrol etmek için
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    // Kategorileri API'den çek
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/categories'); // Kategori endpointi
        // setCategories(response.data); // Geçici olarak yorum satırına alın
        console.log("Categories:", response.data); // Verileri kontrol etmek için
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchProducts();
    fetchCategories();
  }, []);

  return (
    <div className="home-container">
      
      <main className="product-grid">
        {products.map(product => (
          <div className="product-card" key={product.id}>
            <img src={product.imageUrl} alt={product.name} />
            <h3>{product.name}</h3>
            <p className="price">{product.price} AZN</p>
            <p>{product.description}</p>
            <p>{product.category}</p>
          </div>
        ))}
      </main>
    </div>
  );
};

export default Home;
